package box

import (
	"bufio"
	"encoding/base64"
	"fmt"
	"io/ioutil"
	"net/http"
	"os"
	"os/exec"
	"path"
	"strings"

	"git.hener.eu/patrick/htb/helper"
)

var (
	htbdir       string = os.Getenv("HTBDIR")
	htbauthor    string = os.Getenv("HTBAUTHOR")
	htbprofileID string = os.Getenv("HTBPROFILEID")
)

// Images const
const (
	htb_logo      = ""
	htb_crosshair = ""
	htb_star      = ""
)

// Box will hold the box object
type Box struct {
	name      string
	htbdir    string
	lootdir   string
	reportdir string
	imagedir  string
	badgejs   string
	pdf       string
	md        string
}

// New is a convenience method to create new box object
func New(boxname, htbdir string) *Box {
	box := &Box{
		name:      boxname,
		htbdir:    htbdir,
		lootdir:   path.Join(htbdir, "loot", boxname),
		reportdir: path.Join(htbdir, "report", boxname),
		imagedir:  path.Join(htbdir, "report", boxname, "assets"),
		badgejs:   path.Join(htbdir, "report", boxname, "assets", "badge.js"),
		pdf:       path.Join(htbdir, "report", boxname, fmt.Sprintf("%s-writeup.pdf", boxname)),
		md:        path.Join(htbdir, "report", boxname, fmt.Sprintf("%s-writeup.md", boxname)),
	}
	return box
}

// Create will create folder structure and copy template
func (box *Box) Create() error {
	// Check and handle lootdir creation
	if _, err := os.Stat(box.lootdir); os.IsNotExist(err) {
		yes, err := helper.GrabYes(fmt.Sprintf("Lootdir %+v does not exist. Create it? [Y/n]", box.lootdir))
		if err != nil {
			return err
		}
		if yes {
			// Create
			if err := os.MkdirAll(box.lootdir, 0755); err != nil {
				return err
			}
			fmt.Printf("Directory %+v created\n", box.lootdir)
		} else {
			// Do not create and exit
			fmt.Println("Aborting operation. Exiting.")
			os.Exit(3)
		}
	} else {
		fmt.Println("Box already exists. Aborting.")
		os.Exit(5)
	}

	// Check and handle reportdir creation
	if _, err := os.Stat(box.reportdir); os.IsNotExist(err) {
		yes, err := helper.GrabYes(fmt.Sprintf("Reportdir %+v does not exist. Create it? [Y/n]", box.reportdir))
		if err != nil {
			return err
		}
		if yes {
			// Create directory and copy over template assets using helper function
			if err := helper.CopyDir(path.Join(htbdir, "template"), box.reportdir); err != nil {
				return err
			}
			fmt.Printf("Directory %+v created and template files copied over.\n", box.reportdir)
			// Rename template markdown file
			oldName := path.Join(box.reportdir, "htb-writeup-skel.md")
			if err := os.Rename(oldName, box.md); err != nil {
				return err
			}
			// Replace placeholder 'Machine' with actual box name
			read, err := ioutil.ReadFile(box.md)
			if err != nil {
				return err
			}

			newContent := strings.Replace(string(read), "Machine", strings.Title(box.name), -1)
			newContent = strings.Replace(string(newContent), "assets/badge.png", fmt.Sprintf("%s/assets/badge.png", box.reportdir), -1)
			// If env HTBAUTHOR is there replace also the Author name
			if htbauthor != "" {
				newContent = strings.Replace(string(newContent), "Author Name Here", htbauthor, -1)
			}
			if err := ioutil.WriteFile(box.md, []byte(newContent), 0); err != nil {
				return err
			}

			// Fetch Batch image
			if err := fetchBatch(box); err != nil {
				fmt.Printf("Error fetching the banner: %+v\n", err)
			}

			fmt.Printf("I cannot open the vault the first time. So be sure to open a folder as vault in obsidian and navigate it to: '%s'\n", box.reportdir)
		} else {
			// Do not create and exit
			fmt.Println("Aborting operation. Exiting.")
			os.Exit(4)
		}
	} else {
		fmt.Println("Box already exists. Aborting.")
		os.Exit(6)
	}

	return nil
}

// Edit will open the markdown file in an editor using xdg-open
func (box *Box) Edit() error {
	// Check if box was already created
	if _, err := os.Stat(box.reportdir); os.IsNotExist(err) {
		fmt.Println("Box is not there. Create it first.")
		os.Exit(8)
	}

	// Open the md file
	cmd := exec.Command("xdg-open", fmt.Sprintf("obsidian://%s", box.md))
	cmd.Stdout = os.Stdout
	cmd.Stderr = os.Stderr
	err := cmd.Start()
	if err != nil {
		return err
	}

	return nil
}

// Convert will convert markdown to pdf
func (box *Box) Convert() error {
	convert := []string{"pandoc", fmt.Sprintf("--resource-path=%s", box.reportdir), box.md, "--listings", "-H", "listings-setup.tex", "--pdf-engine=xelatex", "--template", "eisvogel", "-f", "markdown", "-t", "latex", "-s", "-o", box.pdf}

	debug := os.Getenv("DEBUG")
	if debug == "TRUE" {
		fmt.Println(convert)
	}

	cmd := exec.Command(convert[0], convert[1:]...)

	stdoutStderror, err := cmd.CombinedOutput()
	if err != nil {
		return err
	}
	fmt.Printf("%s\n", stdoutStderror)
	fmt.Println("Conversion completed successfully")

	yes, err := helper.GrabYes(fmt.Sprintf("Do you want to open %s right away? [Y/n]", box.pdf))
	if err != nil {
		return err
	}
	if yes {
		if err := box.Open(); err != nil {
			return err
		}
	}
	return nil
}

// Open will open the report
func (box *Box) Open() error {
	// Check if box was already created
	if _, err := os.Stat(box.reportdir); os.IsNotExist(err) {
		fmt.Println("Box is not there. Create it first.")
		os.Exit(8)
	}
	// Check if pdf was compiled already
	if _, err := os.Stat(box.pdf); os.IsNotExist(err) {
		yes, err := helper.GrabYes("Markdown was not converted to pdf. Convert it now? [Y/n]")
		if err != nil {
			return err
		}
		if yes {
			if err := box.Convert(); err != nil {
				return err
			}
		} else {
			// Do not create and exit
			fmt.Println("Aborting operation. Exiting.")
			os.Exit(9)
		}
	}
	// Open the pdf
	cmd := exec.Command("xdg-open", box.pdf)
	cmd.Stdout = os.Stdout
	err := cmd.Start()
	if err != nil {
		return err
	}

	return nil
}

// Clear will remove box folders and files
func (box *Box) Clear() error {
	fmt.Println("Do you really want to delete the box content? Then type all upper: YES")
	choice := bufio.NewReader(os.Stdin)
	answer, err := choice.ReadString('\n')

	if err != nil {
		return err
	}

	if answer == "YES\n" {
		if err := os.RemoveAll(box.lootdir); err != nil {
			return err
		}
		if err := os.RemoveAll(box.reportdir); err != nil {
			return err
		}
		fmt.Println("Box was cleared successfully.")

	} else {
		fmt.Println("Nothing was deleted. Aborting.")
		os.Exit(7)
	}

	return nil
}

func fetchBatch(box *Box) error {
	if htbprofileID != "" {
		// Make http request to fetch batch raw response
		resp, err := http.Get(fmt.Sprintf("https://www.hackthebox.eu/badge/%s", htbprofileID))
		if err != nil {
			return err
		}

		// Read in body
		rawBody, err := ioutil.ReadAll(resp.Body)
		if err != nil {
			return err
		}

		// Trim left and right to extraxt pure base64 part
		b64Body := strings.TrimPrefix(string(rawBody), "document.write(window.atob(\"")
		b64Body = strings.TrimSuffix(b64Body, "\"))")

		// Decode base64 to html
		html, err := base64.StdEncoding.DecodeString(b64Body)
		if err != nil {
			return err
		}

		// Replace some things
		htmlStr := strings.Replace(string(html), "<div ", "<div class=\"wrapper\" ", 1)
		htmlStr = strings.ReplaceAll(htmlStr, "https://www.hackthebox.eu/images/screenshot.png", fmt.Sprintf("data:image/png;base64,%s", htb_crosshair))
		htmlStr = strings.ReplaceAll(htmlStr, "_thumb.png", ".png")
		htmlStr = strings.ReplaceAll(htmlStr, "https://www.hackthebox.eu/images/star.png", fmt.Sprintf("data:image/png;base64,%s", htb_star))
		htmlStr = strings.ReplaceAll(htmlStr, "url(https://www.hackthebox.eu/images/icon20.png);", fmt.Sprintf("url('data:image/webp;base64,%s'; background-size: 20px;", htb_logo))

		// Create a empty file
		file, err := os.Create(path.Join(box.imagedir, "badge.html"))
		if err != nil {
			return err
		}
		defer file.Close()

		// Write the bytes to the file
		_, err = file.WriteString(htmlStr)
		if err != nil {
			return err
		}

		// Flush
		err = file.Sync()
		if err != nil {
			return err
		}

		// Insert report image dir as cwd into badge.js and replace it
		read, err := ioutil.ReadFile(box.badgejs)
		if err != nil {
			return err
		}

		newBadgeJs := strings.ReplaceAll(string(read), "%%subcwdhere%%", box.imagedir)

		if err := ioutil.WriteFile(box.badgejs, []byte(newBadgeJs), 0); err != nil {
			return err
		}

		// Now use phantomjs and badge.js to convert html to badge.png
		cmd := exec.Command("phantomjs", box.badgejs)
		cmd.Stdout = os.Stdout
		cmd.Stderr = os.Stderr

		err = cmd.Start()
		if err != nil {
			return err
		}
	}
	return nil
}
